import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import Toast from '../components/Toast';

const Profile = () => {
  const [users, setUsers] = useState([]); // Store all users
  const [filteredUsers, setFilteredUsers] = useState([]); // Store filtered users
  const [skills, setSkills] = useState([]); // Store all available skills
  const [selectedSkills, setSelectedSkills] = useState([]); // Store selected skills
  const [toastMessage, setToastMessage] = useState(''); // Toast message state

  // Fetch users and skills on page load
  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setToastMessage('Authentication token not found. Please log in again.');
          return;
        }

        const [usersResponse, skillsResponse] = await Promise.all([
          axios.get('/users', { headers: { Authorization: `Bearer ${token}` } }),
          axios.get('/skills', { headers: { Authorization: `Bearer ${token}` } }),
        ]);

        setUsers(usersResponse.data.users || []);
        setFilteredUsers(usersResponse.data.users || []);
        setSkills(skillsResponse.data.skills || []);
        setToastMessage('Data fetched successfully!');
      } catch (error) {
        console.error('Error fetching data:', error.message);
        setToastMessage('Failed to fetch data.');
      }
    };

    fetchData();
  }, []);

  // Filter users based on selected skills
  useEffect(() => {
    if (selectedSkills.length === 0) {
      setFilteredUsers(users); // Show all users if no skills are selected
    } else {
      const filtered = users.filter((user) =>
        user.skills.some((skill) => selectedSkills.includes(skill))
      );
      setFilteredUsers(filtered);
    }
  }, [selectedSkills, users]);

  // Handle skill selection
  const handleSkillSelection = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
    setSelectedSkills(selectedOptions);
  };

  // Contact user function
  const contactUser = (user) => {
    window.location.href = `mailto:${user.email}?subject=Contact%20from%20SkillShare&body=Hi%20${user.name},`;
  };

  return (
    <div className="card">
      <h1>Profile</h1>
      <p>
        go back to <a href="/Dashboard">dashboard!</a>
      </p>
      <p>Here are all the registered users:</p>

      {/* Toast Notification */}
      {toastMessage && <Toast message={toastMessage} onClose={() => setToastMessage('')} />}

      {/* Skills Filter */}
      <div style={{ margin: '20px 0' }}>
        <label htmlFor="skills">Filter by Skills:</label>
        <select
          id="skills"
          multiple
          onChange={handleSkillSelection}
          style={{
            width: '100%',
            maxWidth: '300px',
            margin: '10px auto',
            padding: '10px',
            borderRadius: '5px',
            border: '1px solid #ddd',
          }}
        >
          {skills.map((skill, index) => (
            <option key={index} value={skill}>
              {skill}
            </option>
          ))}
        </select>
      </div>

      {/* User Cards */}
      <ul>
        {filteredUsers.length > 0 ? (
          filteredUsers.map((user) => (
            <li
              key={user._id}
              style={{
                marginBottom: '20px',
                padding: '10px',
                border: '1px solid #ddd',
                borderRadius: '5px',
                listStyle: 'none',
                background: '#fff',
                display: 'flex',
                alignItems: 'center',
              }}
            >
              <img
                src={user.photo || '/default-profile.png'}
                alt={user.name}
                style={{ width: '50px', height: '50px', borderRadius: '50%', marginRight: '15px' }}
              />
              <div style={{ flexGrow: 1 }}>
                <strong>{user.name}</strong> ({user.email})
                <p>
                  <i>{user.description || 'No description available.'}</i>
                </p>
                <strong>Skills:</strong>
                <ul style={{ paddingLeft: '20px', textAlign: 'left' }}>
                  {user.skills.length > 0 ? (
                    user.skills.map((skill, index) => <li key={index}>{skill}</li>)
                  ) : (
                    <p>No skills added yet.</p>
                  )}
                </ul>
              </div>
              <button className="btn" onClick={() => contactUser(user)}>
                Contact
              </button>
            </li>
          ))
        ) : (
          <p>No users found.</p>
        )}
      </ul>
    </div>
  );
};

export default Profile;
