import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import Toast from '../components/Toast';

const Profile = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]); // For filtered users
  const [skills, setSkills] = useState([]); // For available skills
  const [selectedSkills, setSelectedSkills] = useState([]); // For selected skills
  const [toastMessage, setToastMessage] = useState('');
  const defaultProfilePic = '/default-profile.jpg'; // Path to the default profile picture

  // Fetch all users and skills on component load
  useEffect(() => {
    const fetchUsersAndSkills = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setToastMessage('Authentication token not found. Please log in again.');
          return;
        }

        // Fetch users
        const usersResponse = await axios.get('/users', {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Fetch skills
        const skillsResponse = await axios.get('/skills', {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (usersResponse.data && usersResponse.data.users) {
          setUsers(usersResponse.data.users);
          setFilteredUsers(usersResponse.data.users); // Set initial filtered users
        }

        if (skillsResponse.data && skillsResponse.data.skills) {
          setSkills(skillsResponse.data.skills);
        }

        setToastMessage('Data fetched successfully!');
      } catch (error) {
        console.error('Error fetching users or skills:', error.response || error.message);
        setToastMessage('Failed to fetch data.');
      }
    };

    fetchUsersAndSkills();
  }, []);

  // Filter users based on selected skills
  useEffect(() => {
    if (selectedSkills.length === 0) {
      setFilteredUsers(users); // Show all users if no skills are selected
    } else {
      const filtered = users.filter((user) =>
        user.skills.some((skill) => selectedSkills.includes(skill))
      );
      setFilteredUsers(filtered);
    }
  }, [selectedSkills, users]);

  const contactUser = (user) => {
    window.location.href = `mailto:${user.email}?subject=Contact%20from%20SkillShare&body=Hi%20${user.name},`;
  };

  const handleSkillSelection = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
    setSelectedSkills(selectedOptions);
  };

  return (
    <div className="card">
      <h1>Profile</h1>
      <p>
        go back to<a href="/Dashboard"> dashboard!</a>
      </p>
      <p>Here are all the registered users:</p>

      {toastMessage && <Toast message={toastMessage} onClose={() => setToastMessage('')} />}

      {/* Skills Filter */}
      <div style={{ marginBottom: '20px' }}>
        <label htmlFor="skills">Filter by Skills:</label>
        <select
          id="skills"
          multiple
          onChange={handleSkillSelection}
          style={{
            width: '100%',
            maxWidth: '300px',
            padding: '10px',
            margin: '10px auto',
            border: '1px solid #ddd',
            borderRadius: '5px',
          }}
        >
          {skills.map((skill, index) => (
            <option key={index} value={skill}>
              {skill}
            </option>
          ))}
        </select>
      </div>

      <ul className="profile-list">
        {filteredUsers.length > 0 ? (
          filteredUsers.map((user) => (
            <li
              className="profile-card"
              key={user._id}
              style={{
                marginBottom: '20px',
                padding: '10px',
                border: '1px solid #ddd',
                borderRadius: '5px',
                listStyle: 'none',
                background: '#fff',
              }}
            >
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <img
                  className="profile-image"
                  src={user.photo || defaultProfilePic} // Use defaultProfilePic if photo is not set
                  alt="Profile"
                  style={{
                    width: '50px',
                    height: '50px',
                    borderRadius: '50%',
                    marginRight: '10px',
                  }}
                />
                <div className="profile-details">
                  <strong>{user.name}</strong> ({user.email})
                  <p>
                    <i>{user.description || 'No description available.'}</i>
                  </p>
                  <ul className="skills-list" style={{ paddingLeft: '20px', textAlign: 'left' }}>
                    {user.skills.map((skill, index) => (
                      <li key={index}>{skill}</li>
                    ))}
                  </ul>
                </div>
              </div>
              <button className="btn" onClick={() => contactUser(user)}>
                Contact
              </button>
            </li>
          ))
        ) : (
          <p>No users found.</p>
        )}
      </ul>
    </div>
  );
};

export default Profile;
