import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import Toast from '../components/Toast';

const Profile = () => {
  const [users, setUsers] = useState([]);
  const [skills, setSkills] = useState([]);
  const [selectedSkills, setSelectedSkills] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [toastMessage, setToastMessage] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setToastMessage('Authentication token not found.');
          return;
        }

        const [usersResponse, skillsResponse] = await Promise.all([
          axios.get('/users', { headers: { Authorization: `Bearer ${token}` } }),
          axios.get('/skills', { headers: { Authorization: `Bearer ${token}` } }),
        ]);

        setUsers(usersResponse.data.users || []);
        setSkills(skillsResponse.data.skills || []);
        setFilteredUsers(usersResponse.data.users || []);
      } catch (error) {
        console.error('Error fetching data:', error.message);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (selectedSkills.length === 0) {
      setFilteredUsers(users);
    } else {
      const filtered = users.filter((user) =>
        user.skills.some((skill) => selectedSkills.includes(skill))
      );
      setFilteredUsers(filtered);
    }
  }, [selectedSkills, users]);

  const handleSkillSelection = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
    setSelectedSkills(selectedOptions);
  };

  return (
    <div className="card">
      <h1>Profile</h1>
      {toastMessage && <Toast message={toastMessage} onClose={() => setToastMessage('')} />}
      <label htmlFor="skills">Filter by Skills:</label>
      <select id="skills" multiple onChange={handleSkillSelection}>
        {skills.map((skill, index) => (
          <option key={index} value={skill}>
            {skill}
          </option>
        ))}
      </select>
      <ul>
        {filteredUsers.map((user) => (
          <li key={user._id}>
            <img src={user.photo} alt="Profile" />
            <h2>{user.name}</h2>
            <p>{user.email}</p>
            <p>{user.description}</p>
            <ul>
              {user.skills.map((skill, index) => (
                <li key={index}>{skill}</li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Profile;
