import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import Toast from '../components/Toast';

const Profile = () => {
  const [users, setUsers] = useState([]); // Store all users
  const [selectedUserSkills, setSelectedUserSkills] = useState([]); // Store skills of a selected user
  const [selectedUser, setSelectedUser] = useState(null); // Currently selected user for skills
  const [toastMessage, setToastMessage] = useState(''); // Toast message state

  // Fetch all users on page load
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const token = localStorage.getItem('token'); // Ensure user is authenticated
        if (!token) {
          throw new Error('Authentication token not found. Please log in again.');
        }

        const response = await axios.get('/users', {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (response.data && response.data.users) {
          setUsers(response.data.users); // Assuming `users` is the array of all users
          setToastMessage('Users fetched successfully!');
        } else {
          setToastMessage('No users found.');
        }
      } catch (error) {
        console.error('Error fetching users:', error.response || error.message);
        setToastMessage(
          'Error: ' + (error.response?.data?.message || 'Failed to fetch users.')
        );
      }
    };

    fetchUsers();
  }, []);

  // Fetch skills for a specific user
  const viewSkills = async (userId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setToastMessage('Authentication token not found. Please log in again.');
        return;
      }

      const response = await axios.get(`/skills/user/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setSelectedUserSkills(response.data.skills || []); // Assuming `skills` is the array of user skills
      setSelectedUser(userId);
      setToastMessage('Skills fetched successfully!');
    } catch (error) {
      console.error('Error fetching user skills:', error.response || error.message);
      setToastMessage(
        'Error: ' + (error.response?.data?.message || 'Failed to fetch user skills.')
      );
    }
  };

  const contactUser = (user) => {
    // Simple contact functionality
    alert(`Contacting ${user.name}. Email: ${user.email}`);
  };

  return (
    <div className="card">
      <h1>Profile</h1>
      <p>Here are all the registered users:</p>

      {/* Toast Notification */}
      {toastMessage && (
        <Toast message={toastMessage} onClose={() => setToastMessage('')} />
      )}

      <ul>
        {users.length > 0 ? (
          users.map((user) => (
            <li key={user._id} style={{ marginBottom: '20px' }}>
              <strong>{user.name}</strong> ({user.email})
              <div style={{ marginTop: '10px' }}>
                <button
                  className="btn"
                  onClick={() => viewSkills(user._id)}
                >
                  View Skills
                </button>
                <button
                  className="btn"
                  onClick={() => contactUser(user)}
                  style={{ marginLeft: '10px' }}
                >
                  Contact
                </button>
              </div>
            </li>
          ))
        ) : (
          <p>No users found.</p>
        )}
      </ul>

      {/* Display skills of the selected user */}
      {selectedUser && (
        <div style={{ marginTop: '30px' }}>
          <h2>Skills of {users.find((user) => user._id === selectedUser)?.name}</h2>
          {selectedUserSkills.length > 0 ? (
            <ul>
              {selectedUserSkills.map((skill, index) => (
                <li key={index}>{skill}</li>
              ))}
            </ul>
          ) : (
            <p>No skills available for this user.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Profile;
