import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import Toast from '../components/Toast';

const Profile = () => {
  const [users, setUsers] = useState([]); // Store all users
  const [toastMessage, setToastMessage] = useState(''); // Toast message state
  const [skills, setSkills] = useState([]); // All available skills
  const [selectedSkills, setSelectedSkills] = useState([]); // Selected skills for filtering
  const [filteredUsers, setFilteredUsers] = useState([]); // Filtered users

  // Fetch all users and skills on page load
  useEffect(() => {
    const fetchUsersAndSkills = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setToastMessage('Authentication token not found. Please log in again.');
          return;
        }

        console.log('Fetching users and skills with token:', token);

        // Fetch all users
        const usersResponse = await axios.get('/users', {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Fetch all available skills
        const skillsResponse = await axios.get('/skills', {
          headers: { Authorization: `Bearer ${token}` },
        });

        setUsers(usersResponse.data.users || []);
        setSkills(skillsResponse.data.skills || []);
        setFilteredUsers(usersResponse.data.users || []); // Initialize filtered users to all users
        setToastMessage('Data fetched successfully!');
      } catch (error) {
        console.error('Error fetching data:', error.response || error.message);
        setToastMessage('Error fetching data. Please try again later.');
      }
    };

    fetchUsersAndSkills();
  }, []);

  // Filter users based on selected skills
  useEffect(() => {
    if (selectedSkills.length === 0) {
      setFilteredUsers(users); // Show all users if no skills are selected
    } else {
      const filtered = users.filter((user) =>
        user.skills.some((skill) => selectedSkills.includes(skill))
      );
      setFilteredUsers(filtered);
    }
  }, [selectedSkills, users]);

  const contactUser = (user) => {
    // Opens email client with user's email
    window.location.href = `mailto:${user.email}?subject=Contact%20from%20SkillShare&body=Hi%20${user.name},`;
  };

  const handleSkillSelection = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
    setSelectedSkills(selectedOptions);
  };

  return (
    <div className="card">
      <h1>Profile</h1>
      <p>
        go back to<a href="/Dashboard"> dashboard!</a>
      </p>
      <p>Here are all the registered users:</p>

      {/* Toast Notification */}
      {toastMessage && <Toast message={toastMessage} onClose={() => setToastMessage('')} />}

      {/* Skills Filter */}
      <div style={{ margin: '20px 0' }}>
        <label htmlFor="skills">Filter by Skills:</label>
        <select
          id="skills"
          multiple
          onChange={handleSkillSelection}
          style={{
            width: '100%',
            maxWidth: '300px',
            margin: '10px auto',
            padding: '10px',
            borderRadius: '5px',
            border: '1px solid #ddd',
          }}
        >
          {skills.map((skill, index) => (
            <option key={index} value={skill}>
              {skill}
            </option>
          ))}
        </select>
      </div>

      <ul>
        {filteredUsers.length > 0 ? (
          filteredUsers.map((user) => (
            <li
              key={user._id}
              style={{
                marginBottom: '20px',
                padding: '10px',
                border: '1px solid #ddd',
                borderRadius: '5px',
                listStyle: 'none',
                background: '255, 255, 255, 0.1',
              }}
            >
              <strong>{user.name}</strong> ({user.email})
              <p>
                <i>{user.description || 'No description available.'}</i>
              </p>
              <div>
                <strong>Skills:</strong>
                <ul style={{ paddingLeft: '20px', textAlign: 'left' }}>
                  {user.skills.map((skill, index) => (
                    <li key={index}>{skill}</li>
                  ))}
                </ul>
              </div>
              <div style={{ marginTop: '10px' }}>
                <button className="btn" onClick={() => contactUser(user)}>
                  Contact
                </button>
              </div>
            </li>
          ))
        ) : (
          <p>No users found.</p>
        )}
      </ul>
      <p>
        go back to<a href="/Dashboard"> dashboard!</a>
      </p>
    </div>
  );
};

export default Profile;
