import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import Toast from '../components/Toast';

const Profile = () => {
  const [users, setUsers] = useState([]);
  const [toastMessage, setToastMessage] = useState('');

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setToastMessage('Authentication token not found. Please log in again.');
          return;
        }

        const response = await axios.get('/api/users', {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (response.data && response.data.users) {
          setUsers(response.data.users);
          setToastMessage('Users fetched successfully!');
        } else {
          setToastMessage('No users found.');
        }
      } catch (error) {
        console.error('Error fetching users:', error.response || error.message);
        setToastMessage('Error: ' + (error.response?.data?.message || 'Failed to fetch users.'));
      }
    };

    fetchUsers();
  }, []);

  const contactUser = (user) => {
    window.location.href = `mailto:${user.email}?subject=Contact%20from%20SkillShare&body=Hi%20${user.name},`;
  };

  return (
    <div className="card">
      <h1>Profile</h1>
      <p>Here are all the registered users:</p>

      {toastMessage && <Toast message={toastMessage} onClose={() => setToastMessage('')} />}

      <ul>
        {users.length > 0 ? (
          users.map((user) => (
            <li
              key={user._id}
              style={{
                marginBottom: '20px',
                padding: '10px',
                border: '1px solid #ddd',
                borderRadius: '5px',
                listStyle: 'none',
                background: '#f9f9f9',
                display: 'flex',
                alignItems: 'center',
                gap: '10px',
              }}
            >
              {user.photo && (
                <img
                  src={user.photo}
                  alt={`${user.name}'s photo`}
                  style={{
                    width: '50px',
                    height: '50px',
                    borderRadius: '50%',
                    objectFit: 'cover',
                  }}
                />
              )}
              <div>
                <strong>{user.name}</strong> ({user.email})
                <p>
                  <i>{user.description || 'No description available.'}</i>
                </p>
                {user.skills && user.skills.length > 0 && (
                  <ul>
                    {user.skills.map((skill, index) => (
                      <li key={index}>{skill}</li>
                    ))}
                  </ul>
                )}
                <button className="btn" onClick={() => contactUser(user)}>
                  Contact
                </button>
              </div>
            </li>
          ))
        ) : (
          <p>No users found.</p>
        )}
      </ul>
    </div>
  );
};

export default Profile;
