import React, { useState, useEffect } from 'react';
import axios from '../api/axios';
import Toast from '../components/Toast';

const Settings = () => {
  const [darkMode, setDarkMode] = useState(
    () => localStorage.getItem('darkMode') === 'true'
  );
  const [description, setDescription] = useState('');
  const [toastMessage, setToastMessage] = useState('');

  // Apply dark mode class
  useEffect(() => {
    if (darkMode) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
    localStorage.setItem('darkMode', darkMode);
  }, [darkMode]);

  // Fetch description on load
  useEffect(() => {
    const fetchDescription = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('/users/me', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setDescription(response.data.description || '');
      } catch (error) {
        console.error('Error fetching description:', error.response || error.message);
        setToastMessage('Failed to fetch user description.');
      }
    };

    fetchDescription();
  }, []);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    setToastMessage(darkMode ? 'Dark mode disabled.' : 'Dark mode enabled.');
  };

  const updateDescription = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(
        '/users/me',
        { description },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setDescription(response.data.user.description); // Update description state
      setToastMessage('Description updated successfully!');
    } catch (error) {
      console.error('Error updating description:', error.response || error.message);
      setToastMessage('Failed to update description.');
    }
  };

  return (
    <div className="card">
      <h1>Settings</h1>
      <p>Adjust your preferences below:</p>

      {/* Toast Notification */}
      {toastMessage && (
        <Toast message={toastMessage} onClose={() => setToastMessage('')} />
      )}

      {/* Dark Mode Toggle */}
      <div style={{ marginTop: '20px' }}>
        <label>
          <input
            type="checkbox"
            checked={darkMode}
            onChange={toggleDarkMode}
          />
          Enable Dark Mode
        </label>
      </div>

      {/* User Description Update */}
      <div style={{ marginTop: '20px' }}>
        <label>
          <p>Update Your Description:</p>
          <textarea placeholder='write about yourself or your skills....'
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            rows="4"
            style={{ width: '100%', border: '1px solid #ddd', borderRadius: '5px', padding: '10px' }}
          />
        </label>
        <button
          className="btn"
          style={{ marginTop: '10px' }}
          onClick={updateDescription}
        >
          Save Description
        </button>
      </div>

      {/* Display Current Description */}
      <div style={{ marginTop: '20px' }}>
        <h3>Current Description:</h3>
        <p>{description || 'No description set.'}</p>
      </div>
    </div>
  );
};

export default Settings;
